import React, { useEffect } from 'react';
import { useState } from 'react';
import './CandyCrush.css';
import { themeContext } from '../../context';
import { useContext } from 'react';

const candyColors = ['blue', 'green', 'orange', 'purple', 'red', 'yellow'];
const width = 8;

const Candycrush = () => {
  const theme = useContext(themeContext);
  const darkMode = theme.state.darkMode;

  const checkForColumnOfFour = () => {
    for (let i = 0; i < 39; i++) {
      const columnOfFour = [i, i + width, i + width * 2, i + width * 3];
      const decidedColor = currentColorArrangement[i];
      if (
        columnOfFour.every(
          (square) => currentColorArrangement[square] === decidedColor
        )
      ) {
        columnOfFour.forEach(
          (square) => (currentColorArrangement[square] = '')
        );
      }
    }
  };

  const checkForColumnOfThree = () => {
    for (let i = 0; i < 47; i++) {
      const columnOfThree = [i, i + width, i + width * 2];
      const decidedColor = currentColorArrangement[i];
      if (
        columnOfThree.every(
          (square) => currentColorArrangement[square] === decidedColor
        )
      ) {
        columnOfThree.forEach(
          (square) => (currentColorArrangement[square] = '')
        );
      }
    }
  };
  const checkForRowOfThree = () => {
    for (let i = 0; i < 64; i++) {
      const rowOfThree = [i, i + 1, i + 2];
      const decidedColor = currentColorArrangement[i];
      const notValid = [6, 7, 14, 15, 22, 23, 30, 31, 38, 39, 46, 47, 54, 55, 63, 64];
      if (notValid.includes(i)) continue
      if (
        rowOfThree.every(
          (square) => currentColorArrangement[square] === decidedColor
        )
      ) {
        rowOfThree.forEach(
          (square) => (currentColorArrangement[square] = '')
        );
      }
    }
  };

  const [currentColorArrangement, setColorArrangement] = useState([]);

  const createBoard = () => {
    const randomColorArrangement = [];
    for (let i = 0; i < width * width; i++) {
      const randomColor =
        candyColors[Math.floor(Math.random() * candyColors.length)];
      randomColorArrangement.push(randomColor);
    }
    setColorArrangement(randomColorArrangement);
  };

  useEffect(() => {
    createBoard();
  }, []);

  useEffect(() => {
    const timer = setInterval(() => {
      checkForColumnOfFour();
      checkForColumnOfThree();

      setColorArrangement([...currentColorArrangement]);
    }, 100);

    return () => clearInterval(timer);
  }, [checkForColumnOfFour, checkForColumnOfThree, currentColorArrangement]);

  console.log(currentColorArrangement);

  return (
    <div className='candycrush'>
      <div id='play' className='button play'>
        Play it now!!
      </div>
      <div className='game'>
        {currentColorArrangement.map((candyColors, index) => (
          <img
            key={index}
            style={{ backgroundColor: candyColors }}
            alt={candyColors}
          />
        ))}
      </div>
    </div>
  );
};

export default Candycrush;
