import React, { useEffect } from 'react'
import { useState } from 'react';
import './CandyCrush.css';
import { themeContext } from '../../context';
import { useContext } from 'react';
import { Swiper, useSwiper } from 'swiper/react'

const candyColors = [
    'blue',
    'green',
    'orange',
    'purple',
    'red',
    'yellow'
]
const width = 8;

const Candycrush = () => {
    const theme = useContext(themeContext);
    const darkMode = theme.state.darkMode;

        const locker = useContext(lockContext);
        const locked = locker.state.locked;
        
        const handleClick = () => {
          locker.dispatch({ type: 'toggle' });
    }



    const [currentColorArrangement, setColorArrangement] = useState([]);

    const createBoard = () => {
        const randomColorArrangement = [];
        for (let i = 0; i < width * width; i++) {
            const randomColor = candyColors[Math.floor(Math.random() * candyColors.length)];
            randomColorArrangement.push(randomColor)
        }
        setColorArrangement(randomColorArrangement);
    }

    useEffect(() => {
        createBoard();
    }, [])
    console.log(currentColorArrangement);
  return (
    <div className="candycrush">
        <div id='play' className='button play' onClick={handleClick}>
          Play it now!! 
        </div>
        <div className="game">
            {currentColorArrangement.map((candyColors, index) => (
                <img
                key={index}
                style={{backgroundColor: candyColors}}
                />
            ))}
        </div>
    </div>
  )
}

export default Candycrush
