import React from 'react'
import './Game.css'

const Game = () => {
  const url = 'https://pokeres.bastionbot.org/images/pokemon';
  const pokemons = [
    { id:1, name: 'balbasaur'},
    { id:8, name: 'wartotle'},
    { id:9, name: 'blastoise'},
    { id:6, name: 'charizard'},
  ];
  return (
    <div className="game">
      {pokemons.map((pokemon, index) => {
        return( 
        <div className='pokemon-card' key={index}>
          <div className="inner">
            <div className="front">
              <img src={""+url+"/"+pokemon.id+".png"}
               alt="pokemon"
                width={100}/>
                <div className="back">
                  
                </div>
            </div>
          </div>
        </div>)
      })}
    </div>
  )
}

export default Game
