import React from 'react'
import './Game.css'

export const Game = () => {
  const url = 'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon';
  const pokemons = [
    { id:1, name: 'bulbasaur'},
    { id:8, name: 'wartotle'},
    { id:9, name: 'blastoise'},
    { id:6, name: 'charizard'},
  ];
  const pairOfPokemons = [...pokemons, ...pokemons];
return <div className="game">
      <div className="cards">
      {pokemons.map((pokemon, index) => {
        return( 
        <div className='pokemon-card flipped' key={index}>
          <div className="inner">
            <div className="front">
              <img 
              src={""+url+"/"+ pokemon.id +".png"}
               alt="pokemon"
                width={150}/>
            </div>
            <div className="back"></div>
          </div>
        </div>
        );
      })}
    </div>
  </div>
}
