import React from 'react';
import './Game.css';
import startMariopic from '../../marioPics/startMario.png';
import cloudsImage from '../../marioPics/cloud.png';
import pipeImg from '../../marioPics/pipe.png';

const Game = () => {
  const mario = document.querySelector('.mario');
  const pipe = document.querySelector('.pipe');
  const startMarioButton = document.querySelector('.start-mario');


  const jump = () => {
    mario.classList.add('jump');
    setTimeout(() => {
      mario.classList.remove('jump');
    }, 500);
  };
  document.addEventListener('keydown', jump);

  const startMario = () => {
    pipe.style.animation = '';
    pipe.style.left = '';

    mario.style.animation = '';
    mario.style.bottom = '';

    mario.src =
      'https://gamerview.uai.com.br/wp-content/uploads/2019/12/mario-gif.gif';
    mario.style.width = '130px';
    mario.style.marginLeft = '50px';

    startMarioButton.style.opacity = '0';

    const loop = setInterval(() => {
      const pipePosition = pipe.offsetLeft;
      const marioPosition = +window
        .getComputedStyle(mario)
        .bottom.replace('px', '');

      if (pipePosition <= 140 && pipePosition > 0 && marioPosition < 10) {
        pipe.style.animation = 'none';
        pipe.style.left = '${pipePosition}px';

        mario.style.animation = 'none';
        mario.style.bottom = '${marioPosition}px';

        mario.src =
          'https://static-cdn.jtvnw.net/jtv_user_pictures/f211ee29-d58e-41bb-be32-0e949b4f416d-profile_image-300x300.png';
        mario.style.width = '80px';
        mario.style.marginLeft = '50px';

        startMarioButton.src = pipe;
        startMarioButton.style.opacity = '0.5';
        startMarioButton.style.height = '33px';
        clearInterval(loop);
      }
    }, 10);
  };

  return (
    <div className='game-board'>
      <img
        src={startMariopic}
        alt='clouds'
        className='start-mario'
        onc
      />
      <img src={cloudsImage} alt="clouds" class="clouds"></img>
      <img src={pipeImg} className='pipe' alt='mario-pipe' />
      <img
        src='https://gamerview.uai.com.br/wp-content/uploads/2019/12/mario-gif.gif'
        className='mario'
        alt='marioo'
      />
    </div>
  );
};

export default Game;
