import React from 'react';

const NewsItem = ({ title, description, url, urlToImage }) => {
  return (
    <div className='news-item'>
      <img className='news' src={urlToImage} alt='new image' />
      <h3>
        <a href={url}>{title}</a>
      </h3>
      <p>{description}</p>
    </div>
  );
};

export default NewsItem;
